chrome.runtime.onMessage.addListener(function (message, sender, sendResponse) {    
    if (message.action === 'widescreen') {
        const chatboxMain = document.querySelector('#root > div.apppage > div:nth-child(1)');
        const chatboxTop = document.querySelector('#root > div.apppage > div:nth-child(1) > div.container-fluid.chattop');
        const chatboxBottom = document.querySelector('#root > div.apppage > div:nth-child(1) > div.container-fluid.chatbottom');
        if (chatboxMain.style.maxWidth === '824px') {
            chatboxMain.style.maxWidth = '9999px';
            chatboxTop.style.maxWidth = '9999px';
            chatboxBottom.style.maxWidth = '9999px';
            chatboxMain.scroll(0, chatboxMain.scrollHeight);
        }
        else {
            chatboxMain.style.maxWidth = '824px';
            chatboxTop.style.maxWidth = '824px';
            chatboxBottom.style.maxWidth = '824px';
        }
        
        sendResponse({ status: 'success' });
    }

    if (message.action === 'rainbowText') {
        rainbowText = !rainbowText;
        if (rainbowText === true) {
            const allElements = document.querySelectorAll('*');

            allElements.forEach((element) => {
                const animations = element.style.animation.split(', ');
                if (!animations.includes('rainbow')) {
                    element.style.animation = 'rainbow 30s infinite';
                }
                element.style.animationPlayState = 'running'; // Ensure animation is running
            });
        } else {
            const allElements = document.querySelectorAll('*');

            allElements.forEach((element) => {
                element.style.animation = 'none';
            });
        }
    }

    if (message.action === 'toggleBG') {
        if (document.body.style.backgroundColor === 'transparent') {
            document.body.style.backgroundColor = 'rgba(36, 37, 37, 1)';
        }
        else {
            document.body.style.backgroundColor = 'transparent';
        }
    }

    if (message.action === 'anonymousMode') {
        if (document.title === 'character.ai') {

        }
        else {
            document.title = 'Anonymous Mode Enabled'
        }

        sendResponse({ status: 'success' });
    }
});

let rainbowText = false;
let backgroundImage = null;
const processedElements = {};

function createBackgroundImage() {
    if (!backgroundImage) {
        document.body.style.backgroundColor = 'transparent';
        backgroundImage = new Image();

        backgroundImage.id = 'background'
        backgroundImage.src = 'data:image/png;base64,';
        backgroundImage.style.position = 'fixed';
        backgroundImage.style.top = 0;
        backgroundImage.style.left = 0;
        backgroundImage.style.width = '100%';
        backgroundImage.style.height = '100%';
        backgroundImage.style.zIndex = -999;
        backgroundImage.style.pointerEvents = 'none'; // Make the image non-interactive

        document.body.appendChild(backgroundImage);
    }
}

function update() {
    createBackgroundImage();
    backgroundImage = true;
    if (window.location.href.startsWith('https://beta.character.ai/chat') || window.location.href.startsWith('https://plus.character.ai/chat')) {
        if (document.querySelector('#root > div.apppage > div:nth-child(1) > div.container-fluid.chattop > div:nth-child(3) > div.text-danger.row.chatheaderbg-normal > span')) {
            document.querySelector('#root > div.apppage > div:nth-child(1) > div.container-fluid.chattop > div:nth-child(3) > div.text-danger.row.chatheaderbg-normal > span').textContent = 'Remember: Everything Characters say is made up! | 🔧Character.AI: Modifiers by YouTubey!';
        }
    }
    else {
        
    }

    if (rainbowText === true) {
        const allElements = document.querySelectorAll('*');

        allElements.forEach((element) => {
            const animations = element.style.animation.split(', ');
            if (!animations.includes('rainbow')) {
                element.style.animation = 'rainbow 30s infinite';
            }
            element.style.animationPlayState = 'running'; // Ensure animation is running
        });
    } else {
        const allElements = document.querySelectorAll('*');

        allElements.forEach((element) => {
            element.style.animation = 'none';
        });
    }

    requestAnimationFrame(update);
}

requestAnimationFrame(update);